package assignments1;

public class assignDowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=2;
		int j=1;
		
		do {
			int x=i*j;
			System.out.print(i);
			System.out.print("*");
			System.out.print(j);
			System.out.print("=");
			System.out.println(x);
			j++;
		}
		while 
		(j<=10);
		
		int k = 1;
		do 
		{
			System.out.println("2" + " * " + k + " = " + (2 * k));
			
		}
		while (++k<=10);
		

	}

}
