package assignments1;

public class assignment1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int j = 1; j <= 10; j++) {
			int i = 2;
			int x = i * j;
			System.out.print(i);
			System.out.print("*");
			System.out.print(j);
			System.out.print("=");
			System.out.println(x);

		}

	}

}