package assignments1;

public class assignwhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=2;
		int j=1;
		
		while(j<=10) {
			int x =i*j;
			
			System.out.print(i);
			System.out.print("*");
			System.out.print(j);
			System.out.print("=");
			System.out.println(x);
			j++;
		}

	}

}
